import { Fragment, useState } from "react";
import Input from "../UI/Input";
import Button from "../UI/Button";
import quiz from "./../../data/quiz.json";
const TestSection = (props) => {
  const [currentquestion, setcurrentquestion] = useState(quiz[0]);
  const options = ["A", "B", "C", "D"];
  const option = currentquestion.answer.map((x, index) => {
    return (
      <div style={{ display: "flex", marginTop: "10px" }}>
        <p>
          <b>{options[index]} . </b>
        </p>
        <Button className={"button2"}>{x} </Button>
      </div>
    );
  });

  return (
    <Fragment>
      <div style={{ display: "flex" }}>
        <div style={{ width: "80%", padding: "10px" }}>
          <h5>{quiz[0].question} </h5>
          {option}
        </div>
        <div style={{ width: "20%", padding: "10px" }}></div>
      </div>
    </Fragment>
  );
};

export default TestSection;
