import quiz from "./../../data/quiz.json";
const quizArr = [];

class quizdatastructure {
  quizfunc = () => {
    let questionState = {
      index: 0,
      answerSelectedindex: null,
      correctAnswer: null,
      savedforlater: false,
      marked: false,
      attempted: false,
    };

    for (let x of quiz) {
      let singlequizdata = {
        index: x.id,
        question: x.question,
        answerSelectedindex: null,
        correctAnswer: null,
        savedforlater: false,
        marked: false,
        attempted: false,
      };
      quizArr.push(singlequizdata);
    }

    return quizArr;
  };
}
export default quizdatastructure;
