import quiz from "./../../data/quiz.json";
const quizArr = [];
const quizdatastructure = () => {
  let questionState = {
    index: 0,
    answerSelectedindex: null,
    correctAnswer: null,
    savedforlater: false,
    marked: false,
    attempted: false,
  };

  for (let x of quiz) {
    let singlequizdata = {
      index: x.id,
      answerSelectedindex: null,
      correctAnswer: null,
      savedforlater: false,
      marked: false,
      attempted: false,
    };
    quizArr.push(singlequizdata);
  }
};

export default quizArr;
