import { Fragment, useState } from "react";
import Input from "../UI/Input";
import Button from "../UI/Button";
import Classes from "./Test.module.css";
import quiz from "./../../data/quiz.json";
import RightPanel from "./RightPanel";
import BottomPanel from "./BottomPanel";
const TestSection = (props) => {
  const [currentquestionindex, setcurrentquestionindex] = useState(0);
  const options = ["A", "B", "C", "D"];
  const option = quiz[currentquestionindex].answer.map((x, index) => {
    return (
      <div style={{ display: "flex", marginTop: "10px" }}>
        <p>
          <b>{options[index]} . </b>
        </p>
        <Button className={"button2"}>{x} </Button>
      </div>
    );
  });

  const nextQuestion = () => {
    setcurrentquestionindex(currentquestionindex + 1);
  };

  const selectQuestion = (i) => {
    setcurrentquestionindex(i);
  };

  return (
    <Fragment>
      <div className={Classes.wrapper}>
        <div className={Classes.leftpanel}>
          <h5>{quiz[0].question} </h5>
          {option}
        </div>
        <div className={Classes.rightpanel}>
          <RightPanel quiz={quiz} currentindex={currentquestionindex} selectQuestion={selectQuestion} />
        </div>
        <BottomPanel nextQuestion={nextQuestion} />
      </div>
    </Fragment>
  );
};

export default TestSection;
