import { Fragment, useState } from "react";
import Input from "../UI/Input";
import Button from "../UI/Button";
import Classes from "./Test.module.css";
import quiz from "./../../data/quiz.json";
import RightPanel from "./RightPanel";
import BottomPanel from "./BottomPanel";
import LeftPanel from "./LeftPanel";
const TestSection = (props) => {
  let currentquestionState = {
    index: 0,
    answerSelectedindex: null,
    correctAnswer: null,
    savedforlater: false,
    marked: false,
  };
  const [currentquestionindex, setcurrentquestionindex] = useState(0);
  const [questionState, setquestionState] = useState(currentquestionState);
  const nextQuestion = () => {
    setcurrentquestionindex(currentquestionindex + 1);
    let index = questionState.index;
    setcurrentquestionindex({ ...questionState, index: index + 1 });
  };

  const selectQuestion = (i) => {
    setcurrentquestionindex(i);
  };

  const answerselect = (ansIndex) => {
    //let ansselect = answerselect;
    // ansselect["answerSelectedindex"] = ansIndex;
    let qstate = questionState;
    qstate.answerSelectedindex = ansIndex;
    setquestionState(qstate);
    console.log(questionState);
  };

  return (
    <Fragment>
      <div className={Classes.wrapper}>
        <LeftPanel currentquestionindex={currentquestionindex} quiz={quiz} questionState={questionState} answerselect={answerselect} />
        <div className={Classes.rightpanel}>
          <RightPanel quiz={quiz} currentindex={currentquestionindex} selectQuestion={selectQuestion} questionState={questionState} />
        </div>
        <BottomPanel nextQuestion={nextQuestion} />
      </div>
    </Fragment>
  );
};

export default TestSection;
